INSERT INTO saint_biographies (saint_id, bio_line) VALUES (360, 'Christmas celebrates the birth of Jesus Christ in Bethlehem around 4-6 BC, fulfilling Old Testament prophecies.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (360, 'Jesus was born to the Virgin Mary in a stable because there was no room in the inn. Angels announced His birth to shepherds.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (360, 'The Incarnation - God becoming man - is the central mystery of Christianity, celebrated with joy throughout the world.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (361, 'Stephen was one of seven deacons chosen by the Apostles to serve the early Church in Jerusalem.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (361, 'He performed great wonders and signs among the people and preached powerfully about Jesus as the Messiah.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (361, 'Around 34 AD, he was brought before the Sanhedrin, gave a bold testimony, and was stoned to death, becoming Christianity''s first martyr. Saul (later Paul) witnessed his death.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (362, 'John, son of Zebedee and brother of James, was one of Jesus''s closest disciples, part of the inner circle at the Transfiguration and Gethsemane.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (362, 'At the Last Supper, he reclined next to Jesus. At the Cross, Jesus entrusted Mary to his care. He wrote the fourth Gospel, three epistles, and the Book of Revelation.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (362, 'He lived to old age in Ephesus and died around 100 AD, the only apostle not martyred.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (363, 'The Holy Innocents were the male children of Bethlehem, two years old and under, killed by King Herod''s order.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (363, 'Herod, seeking to eliminate the newborn King of the Jews, ordered this massacre. The Holy Family escaped to Egypt.');
INSERT INTO saint_biographies (saint_id, bio_line) VALUES (363, 'These children are venerated as the first martyrs, having died in place of Christ.');
